import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [imageData, setImageData] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return;

    setIsUploading(true);
    setStatus('Uploading file...');

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setStatus('Processing completed!');
      setImageData(response.data);
    } catch (error) {
      setStatus('Error uploading file.');
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="App">
      <h1>PDF to Image Extractor</h1>

      <input type="file" accept=".pdf" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={isUploading}>
        {isUploading ? 'Uploading...' : 'Upload PDF'}
      </button>

      {status && <p>{status}</p>}

      {imageData && (
        <div>
          <h2>Extracted Data:</h2>
          <p>PDF Size: {imageData.pdfSize / 1024} KB</p>
          <p>Total Images Extracted: {imageData.totalImages}</p>
          <p>Combined Image Size: {imageData.combinedImageSize / 1024} KB</p>

          <div className="image-gallery">
            {imageData.imagePaths.map((image, index) => (
              <img key={index} src={`http://localhost:5000/${image}`} alt={`Extracted ${index}`} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
